var x1;
var x2;
var x3;
var x4;
var x5;

//Create the audio tag
var corr = document.createElement("audio");
corr.preload = "auto";
var wrong = document.createElement("audio");
wrong.preload = "auto";

//Load the sound file (using a source element for expandability)
var src = document.createElement("source");
src.src = "correct.mp3";
var src2 = document.createElement("source");
src2.src = "wrong.mp3";

corr.appendChild(src);
wrong.appendChild(src2);

//Load the audio tag
//It auto plays as a fallback
corr.load();
corr.volume = 0.000000;
corr.play();

wrong.load();
wrong.volume = 0.000000;
wrong.play();

//Plays the sound
function play(sound) {
   //Set the current time for the audio file to the beginning
   sound.currentTime = 0.01;
   sound.volume = 1;

   //Due to a bug in Firefox, the audio needs to be played after a delay
   setTimeout(function(){sound.play();},1);
}


var choose = function () {
	return Math.floor(Math.random() * 455);
}

window.onload = function() {
	x1 = choose();
	x2 = choose();
	x3 = choose();
	x4 = choose();
	x5 = choose();
	document.getElementById("pok1").innerHTML = data["V2"][x1];
	document.getElementById("pok2").innerHTML = data["V2"][x2];
	document.getElementById("pok3").innerHTML = data["V2"][x3];
	document.getElementById("pok4").innerHTML = data["V2"][x4];
	document.getElementById("pok5").innerHTML = data["V2"][x5];
}

function checkSols() {
	var num1 = parseInt(document.getElementById("a").value);
	var num2 = parseInt(document.getElementById("b").value);
	var num3 = parseInt(document.getElementById("c").value);
	var num4 = parseInt(document.getElementById("d").value);
	var num5 = parseInt(document.getElementById("e").value);
	var correct = true;
	var guesses = [num1, num2, num3, num4, num5];
	var sol = [x1, x2, x3, x4, x5];

	var e = document.getElementById("moder");
	var mode = e.options[e.selectedIndex].value;

	if (mode == 1) {
		for (var i = 1; i < 5; i++) {
			var keep = guesses.indexOf(i);
			var keep2 = guesses.indexOf(i + 1);
			if (keep === -1 || keep2 === -1 || data['V1'][sol[keep]] < data['V1'][sol[keep2]]) {
				correct = false;
				break;
			}
		}
	} else {
		for (var i = 0; i < 5; i++) {
			if (guesses[i] != data["V1"][sol[i]]) {
				correct = false;
				break;
			}
		}
	}

	if (correct) {
		document.getElementById("img").style.display = "block";
		if (document.getElementById("sound").checked == true) {
			play(corr);
		}
	} else {
		document.getElementById("img").style.display = "none";
	}
}

